import { Component } from '@angular/core';
import * as data from '../../../../shared/data/data/ui-kits/progres-bar';

@Component({
  selector: 'app-custom-height-progress',
  templateUrl: './custom-height-progress.component.html',
  styleUrls: ['./custom-height-progress.component.scss']
})

export class CustomHeightProgressComponent {

  public customHeightProgressBarData = data.customHeightProgressBarData;

}

@for (allData of customHeightProgressBarData; track allData) {
<div class="card">
    <div class="card-header">
        <h4>{{allData.header}}</h4>
        <p class="f-m-light mt-1" [innerHTML]="allData.desc"></p>
    </div>
    <div class="card-body progress-showcase">
        <div class="row">
            <div class="col">
                @for (data of allData.data; track data) {
                <div class="progress" style="height: 1px;" [ngStyle]="{ height : data.height }">
                    <div class="progress-bar bg-{{data.colorClass}}" [ngStyle]="{ width : data.width }"></div>
                </div>
                }
            </div>
        </div>
    </div>
</div>
}
