import { Component } from '@angular/core';
import { largeProgressBarData } from '../../../../shared/data/data/ui-kits/progres-bar';

@Component({
  selector: 'app-large-progress-bars',
  templateUrl: './large-progress-bars.component.html',
  styleUrls: ['./large-progress-bars.component.scss']
})

export class LargeProgressBarsComponent {

  public largeProgressBarData = largeProgressBarData;


}

@for (allData of largeProgressBarData; track allData) {
<div class="card height-equal">
    <div class="card-header">
        <h4>{{allData.header}}</h4>
        <p class="f-m-light mt-1" [innerHTML]="allData.desc"></p>
    </div>
    <div class="card-body progress-showcase">
        <div class="row">
            <div class="col">
                @for (data of allData.data; track data) {
                <div class="progress lg-progress-bar">
                    <div class="progress-bar bg-{{data.colorClass}}" role="progressbar"
                        [ngStyle]="{ width : data.width }">{{data.width}}</div>
                </div>
                }
            </div>
        </div>
    </div>
</div>
}
