import { Component } from '@angular/core';
import { questionData } from '../../../shared/data/data/faq/faq';
import { NgbAccordionConfig } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-questions',
  templateUrl: './questions.component.html',
  styleUrls: ['./questions.component.scss']
})

export class QuestionsComponent {

  public questionData = questionData;

  constructor(config: NgbAccordionConfig) {
		config.closeOthers = true;
	}

}

@for (allData of questionData; track allData) {
<div>
  @if (allData.heading) {
  <div class="faq-title">
    <h5>{{allData.heading}}</h5>
  </div>
  }
  <div ngbAccordion [closeOthers]="true">
    @for (data of allData.data; track data) {
    <div class="card" ngbAccordionItem [collapsed]="data.panel !== 'First'">
      <div class="card-header" ngbAccordionHeader>
        <h5 class="mb-0">
          <button class="btn btn-link collapsed ps-0" ngbAccordionButton>
            <app-feather-icons [icons]="'help-circle'"></app-feather-icons>
            {{data.title}}</button>
        </h5>
      </div>
      <div class="collapse" ngbAccordionCollapse>
        <div class="card-body" ngbAccordionBody> <ng-template>
            {{ data.desc }}
          </ng-template></div>
      </div>
    </div>
    }
  </div>
</div>
}
