import { Component, Input } from '@angular/core';
import * as data  from '../../../../shared/data/data/ui-kits/helper-class';

@Component({
  selector: 'app-comman-helper-class',
  templateUrl: './comman-helper-class.component.html',
  styleUrls: ['./comman-helper-class.component.scss']
})

export class CommanHelperClassComponent {

  @Input()data : data.commonHelperClasses [];

}

@for (item of data; track item) {
<div class="card">
  <div class="card-header card-no-border pb-0">
    <h4>{{item.header}}</h4>
    <p class="f-m-light mt-1" [innerHTML]="item.desc"></p>
  </div>
  <div class="card-body">
    <div class="row g-3">
      @for (items of item.titleData; track items) {
      <div class="{{items.colClass}}">
        <div class="{{items.divClass}}">
          <h5 class="f-w-600 mb-3">{{items.title}}</h5>
          @for (datas of items.data; track datas) {
            <div class="helper-common-box">
              <div class="{{datas.textClass}}">{{datas.textC}}</div><span>{{datas.text}}</span>
            </div>
          }
        </div>
      </div>
      }
    </div>
  </div>
</div>
}
