        <li class="back-btn">
          <a href="javascript:void(0)">
            <img class="logo_icon" src="assets\images\logo\juno_logo_icon_36x37.png" alt="">
          </a>
          <div class="mobile-back text-end" (click)="sidebarToggle()">
