import { Component } from '@angular/core';
import * as data from '../../../../shared/data/data/ui-kits/progres-bar';

@Component({
  selector: 'app-basic-progress-bars',
  templateUrl: './basic-progress-bars.component.html',
  styleUrls: ['./basic-progress-bars.component.scss']
})

export class BasicProgressBarsComponent {

  public basicProgressBarData = data.basicProgressBarData;

}

@for (allData of basicProgressBarData; track allData) {
<div class="card">
    <div class="card-header">
        <h4>{{ allData.header }}</h4>
        <p class="f-m-light mt-1" [innerHTML]="allData.desc"></p>
    </div>
    <div class="card-body progress-showcase">
        <div class="row">
            <div class="col">
                @for (data of allData.data; track data) {
                <div class="progress">
                    <div class="progress-bar bg-{{data.colorClass}}" [ngStyle]="{width : data.width }">{{data.width}}
                    </div>
                </div>
                }
            </div>
        </div>
    </div>
</div>
}
